mapper.select:
SELECT beanId, beanName, b.authorName, c.cateName, beanStock, a.regDate FROM beans_mall_bean a LEFT OUTER JOIN beans_mall_author b ON a.authorId = b.authorId LEFT OUTER JOIN beans_mall_cate c ON a.cateCode = c.cateCode
WHERE (beanName LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY beanId DESC LIMIT #{skip}, #{amount}

SELECT orderId, memberId, orderState, orderDate FROM beans_mall_order
WHERE memberId LIKE CONCAT('%', #{keyword}, '%')
ORDER BY orderDate DESC LIMIT #{skip}, #{amount}
